import React from "react";
import { Snackbar } from "@mui/material";
import { Alert } from "./alert";
const Toast = ({ onClose, title, severity = "warning", autoHideDuration = 6000, anchorOrigin = { vertical: "bottom", horizontal: "right" }, children, }) => {
    const [open, setOpen] = React.useState(true);
    const onDismiss = () => {
        if (open && onClose !== undefined) {
            onClose();
        }
        setOpen(false);
    };
    return (React.createElement(Snackbar, { open: open, autoHideDuration: autoHideDuration, anchorOrigin: anchorOrigin, onClose: (_, reason) => {
            // This way it will not auto close when clicking in the window, will instead wait on the timeout or onClose
            if (reason !== "clickaway") {
                onDismiss();
            }
        } },
        React.createElement("div", null,
            React.createElement(Alert, { elevation: 6, variant: "filled", onClose: onClose ? onDismiss : null, severity: severity, title: title }, children))));
};
export default Toast;
